/* css.reset */
* {
  box-sizing: border-box;
}
*:before,
*:after {
  box-sizing: border-box;
}
* {
  margin: 0;
  padding: 0;
}
ul,
ol {
  list-style: none;
}
a {
  color: inherit;
  text-decoration: none;
}
img {
  max-width: 100%;
  max-height: 100%;
  border-radius: 50%;
}
/* style */
body {
  background-color: #eee;
}
header {
  margin: 20px;
}
.nav {
  display: flex;
  flex-flow: row nowrap;
  justify-content: space-between;
  position: sticky;
  position: -webkit-sticky;
  top: 0;
  max-width: 900px;
  margin-right: auto;
  margin-left: auto;
}
.nav li {
  width: 100%;
  background: rgb(231, 228, 228);
  border-radius: 5px;
  margin: 0 1px 10px 1px;
  line-height: 30px;
  display: flex;
  justify-content: center;
}
.searchForm {
  display: flex;
  justify-content: space-between;
}

.globalMain {
  max-width: 900px;
  margin-left: auto;
  margin-right: auto;
}
.searchForm > input {
  width: 100%;
  margin-right: 10px;
  height: 40px;
  padding: 0 10px;
  border: 1px solid #ddd;
  border-radius: 5px;
  font-size: 100%;
}
.searchForm > button {
  white-space: nowrap;
  padding: 0 27px;
  border: none;
  background: #1a97df;
  border-radius: 4px;
  color: #fff;
  font-size: 18px;
}
.listname {
  font-size: 22px;
  margin: 0 24px;
  padding-bottom: 5px;
  border-bottom: 1px solid #ddd;
}

.siteList {
  display: flex;
  margin: 16px;
  margin-top: 6px;
  flex-flow: row wrap;
  justify-content: space-between;
  border-radius: 5px;
  padding: 0;
}
.siteList > li {
  margin-bottom: 10px;
}
.siteList .site {
  background: white;
  width: 160px;
  height: 120px;
  display: flex;
  justify-content: center;
  align-items: center;
  flex-direction: column;
  border: 1px solid #ddd;
  border-radius: 5px;
  padding: 20px 0;
  position: relative;
  cursor: pointer;
}
.siteList .site > .logo {
  width: 64px;
  height: 64px;
  display: flex;
  justify-content: center;
  align-items: center;
  font-size: 64px;
}
.siteList .site > .link {
  font-size: 14px;
  margin-top: 4px;
}
.siteList .site > .close {
  position: absolute;
  right: 4px;
  top: 0;
  display: none;
  cursor: default;
}
.siteList .site:hover > .close {
  display: block;
}
.addButton .icon {
  width: 64px;
  height: 64px;
}
@media (min-width: 500px) {
  /*500以下不管*/
  .searchForm {
    max-width: 600px;
    margin-left: auto;
    margin-right: auto;
  }
  .siteList {
    margin-left: 0;
    justify-content: start;
    margin-right: -25px;
  }
  .siteList > li {
    margin-right: 25px;
  }
}
